import React, { useState, useEffect } from 'react';
import { startSession, completeSession } from '../services/api';
import './Schedule.css';

function Schedule({ schedule, scheduleId, isLoading, onAdjust }) {
  const [expandedDay, setExpandedDay] = useState(null);
  const [showSettings, setShowSettings] = useState(false);
  const [progress, setProgress] = useState({ completedSessions: [], percentComplete: 0 });

  useEffect(() => {
    if (schedule?.progress) {
      setProgress(schedule.progress);
    }
  }, [schedule]);

  if (isLoading) {
    return (
      <div className="schedule-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Creating your personalized study schedule...</p>
          <p className="loading-subtext">Analyzing content and optimizing learning path</p>
        </div>
      </div>
    );
  }

  if (!schedule) return null;

  const { schedule: days, studyTips, milestones, totalEstimatedHours } = schedule;

  const toggleDay = (dayNum) => setExpandedDay(expandedDay === dayNum ? null : dayNum);

  const handleStart = async (day, sessionIndex) => {
    await startSession(scheduleId, day, sessionIndex);
  };

  const handleComplete = async (day, sessionIndex) => {
    const res = await completeSession(scheduleId, day, sessionIndex);
    if (res.success) setProgress(res.progress);
  };

  const getTypeIcon = (type) => ({ reading: 'üìñ', practice: '‚úçÔ∏è', quiz: 'üéØ', review: 'üîÑ' }[type] || 'üìö');
  const getPriorityColor = (priority) => ({ high: '#f56565', medium: '#ed8936', low: '#48bb78' }[priority] || '#718096');

  const formatDuration = (minutes) => {
    const h = Math.floor(minutes / 60);
    const m = minutes % 60;
    return h > 0 ? (m > 0 ? `${h}h ${m}m` : `${h}h`) : `${m}m`;
  };

  const totalDays = days.length;
  const completionPercentage = progress.percentComplete || 0;

  const isSessionCompleted = (day, sessionIndex) =>
    progress.completedSessions.some(s => s.day === day && s.sessionIndex === sessionIndex);

  return (
    <div className="schedule-container">
      {/* Header Stats */}
      <div className="schedule-header">
        <div className="header-content">
          <h2>üìÖ Your Study Schedule</h2>
          <button className="settings-button" onClick={() => setShowSettings(!showSettings)}>
            ‚öôÔ∏è Adjust
          </button>
        </div>

        <div className="schedule-stats">
          <div className="stat-card">
            <span className="stat-label">Total Time</span>
            <span className="stat-value">{totalEstimatedHours}h</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Duration</span>
            <span className="stat-value">{totalDays} days</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Progress</span>
            <span className="stat-value">{completionPercentage}%</span>
          </div>
        </div>
      </div>

      {/* Settings Panel */}
      {showSettings && (
        <div className="settings-panel">
          <h3>Adjust Schedule</h3>
          <div className="settings-options">
            <button onClick={() => onAdjust('more-time')}>Need More Time</button>
            <button onClick={() => onAdjust('faster-pace')}>Faster Pace</button>
            <button onClick={() => onAdjust('more-practice')}>More Practice Sessions</button>
          </div>
        </div>
      )}

      {/* Timeline View */}
      <div className="schedule-timeline">
        {days.map(day => {
          const isExpanded = expandedDay === day.day;
          const hasMilestone = milestones?.find(m => m.day === day.day);

          return (
            <div key={day.day} className={`day-card ${isExpanded ? 'expanded' : ''}`}>
              <div className="day-header" onClick={() => toggleDay(day.day)}>
                <div className="day-info">
                  <span className="day-number">Day {day.day}</span>
                  <span className="day-date">
                    {new Date(day.date).toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' })}
                  </span>
                </div>
                <div className="day-summary">
                  <span className="session-count">{day.sessions.length} sessions</span>
                  <span className="day-duration">{formatDuration(day.totalMinutes)}</span>
                  <span className="expand-icon">{isExpanded ? '‚ñº' : '‚ñ∂'}</span>
                </div>
              </div>

              {hasMilestone && <div className="milestone-badge">üèÜ Milestone: {hasMilestone.milestone}</div>}

              <div className="day-goal">
                <strong>Goal:</strong> {day.dailyGoal}
              </div>

              {isExpanded && (
                <div className="day-sessions">
                  {day.sessions.map((session, idx) => {
                    const completed = isSessionCompleted(day.day, idx);
                    return (
                      <div key={idx} className={`session-card ${completed ? 'completed' : ''}`}>
                        <div className="session-header">
                          <span className="session-icon">{getTypeIcon(session.type)}</span>
                          <span className="session-title">{session.title}</span>
                          <span className="session-priority" style={{ backgroundColor: getPriorityColor(session.priority) }}>
                            {session.priority}
                          </span>
                        </div>

                        <div className="session-details">
                          <p className="session-description">{session.description}</p>
                          <div className="session-topics">
                            <strong>Topics:</strong>
                            <div className="topic-tags">
                              {session.topics.map((topic, i) => (
                                <span key={i} className="topic-tag">{topic}</span>
                              ))}
                            </div>
                          </div>
                          <div className="session-meta">
                            <span className="session-duration">‚è±Ô∏è {formatDuration(session.duration)}</span>
                            <span className="session-type">{session.type}</span>
                          </div>
                        </div>

                        <div className="session-actions">
                          <button
                            className="action-button start"
                            disabled={completed}
                            onClick={() => handleStart(day.day, idx)}
                          >
                            Start Session
                          </button>
                          <button
                            className="action-button complete"
                            disabled={completed}
                            onClick={() => handleComplete(day.day, idx)}
                          >
                            {completed ? '‚úÖ Completed' : 'Mark Complete'}
                          </button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* Study Tips */}
      {studyTips && studyTips.length > 0 && (
        <div className="study-tips">
          <h3>üí° Study Tips</h3>
          <ul>{studyTips.map((tip, index) => <li key={index}>{tip}</li>)}</ul>
        </div>
      )}

      {/* Milestones */}
      {milestones && milestones.length > 0 && (
        <div className="milestones-section">
          <h3>üéØ Learning Milestones</h3>
          <div className="milestones-grid">
            {milestones.map((m, i) => (
              <div key={i} className="milestone-card">
                <div className="milestone-day">Day {m.day}</div>
                <div className="milestone-title">{m.milestone}</div>
                <div className="milestone-assessment">{m.assessment}</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Schedule;
