import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

/**
 * Upload a PDF file
 * @param {File} file - The PDF file to upload
 * @returns {Promise} Upload response with file details
 */
export const uploadFile = async (file) => {
  const formData = new FormData();
  formData.append('file', file);

  const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });

  return response.data;
};

/**
 * Generate summary from uploaded file
 * @param {string} filePath - Path to the uploaded file
 * @param {string} customPrompt - Optional custom instructions
 * @returns {Promise} Summary data
 */
export const generateSummary = async (filePath, customPrompt = '') => {
  const response = await axios.post(`${API_BASE_URL}/summarize`, {
    filePath,
    customPrompt,
  });

  return response.data;
};

/**
 * Generate quiz from uploaded file
 * @param {string} filePath - Path to the uploaded file
 * @param {Object} options - Quiz options (numQuestions, difficulty)
 * @returns {Promise} Quiz data
 */
export const generateQuiz = async (filePath, options = {}) => {
  const { numQuestions = 5, difficulty = 'medium' } = options;

  const response = await axios.post(`${API_BASE_URL}/quiz`, {
    filePath,
    numQuestions,
    difficulty,
  });

  return response.data;
};

/**
 * Check API health
 * @returns {Promise} Health status
 */
export const checkHealth = async () => {
  const response = await axios.get('http://localhost:5000/health');
  return response.data;
};
