import React, { useState } from "react";
import { completeSession, startSession } from "../services/api";
import "./Schedule.css";

function Schedule({ schedule, scheduleId, isLoading, onAdjust }) {
  const [expandedDay, setExpandedDay] = useState(null);
  const [showSettings, setShowSettings] = useState(false);

  if (isLoading) return <div className="loading-spinner">Loading...</div>;
  if (!schedule) return null;

  const { schedule: days, studyTips, milestones, totalEstimatedHours } = schedule;

  const toggleDay = (dayNum) => setExpandedDay(expandedDay === dayNum ? null : dayNum);

  const handleStart = async (day, sessionIndex) => { await startSession(scheduleId, day, sessionIndex); };
  const handleComplete = async (day, sessionIndex) => { await completeSession(scheduleId, day, sessionIndex); };

  const getTypeIcon = (type) => ({ reading: "üìñ", practice: "‚úçÔ∏è", quiz: "üéØ", review: "üîÑ" }[type] || "üìö");
  const getPriorityColor = (priority) => ({ high: "#f56565", medium: "#ed8936", low: "#48bb78" }[priority] || "#718096");
  const formatDuration = (minutes) => { const h = Math.floor(minutes/60); const m = minutes % 60; return h > 0 ? (m > 0 ? `${h}h ${m}m` : `${h}h`) : `${m}m`; };

  return (
    <div className="schedule-container">
      <div className="schedule-header">
        <h2>üìÖ Your Study Schedule</h2>
        <button onClick={() => setShowSettings(!showSettings)}>‚öôÔ∏è Adjust</button>
      </div>

      {showSettings && (
        <div className="settings-panel">
          <button onClick={() => onAdjust("more-time")}>Need More Time</button>
          <button onClick={() => onAdjust("faster-pace")}>Faster Pace</button>
          <button onClick={() => onAdjust("more-practice")}>More Practice</button>
        </div>
      )}

      <div className="schedule-timeline">
        {days.map((day, idx) => {
          const isExpanded = expandedDay === day.day;
          return (
            <div key={day.day} className={`day-card ${isExpanded ? "expanded" : ""}`}>
              <div className="day-header" onClick={() => toggleDay(day.day)}>
                <span>Day {day.day}</span>
                <span>{formatDuration(day.totalMinutes)}</span>
                <span>{isExpanded ? "‚ñº" : "‚ñ∂"}</span>
              </div>

              {isExpanded && (
                <div className="day-sessions">
                  {day.sessions.map((s, i) => (
                    <div key={i} className="session-card">
                      <span>{getTypeIcon(s.type)} {s.title}</span>
                      <span style={{ backgroundColor: getPriorityColor(s.priority) }}>{s.priority}</span>
                      <div>
                        <button onClick={() => handleStart(day.day, i)}>Start Session</button>
                        <button onClick={() => handleComplete(day.day, i)}>Mark Complete</button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Schedule;
