import React, { useState, useEffect } from 'react';
import './Schedule.css';
import axios from 'axios';

function Schedule({ schedule, isLoading, onAdjust, userId, materialId, materialTitle, filePath }) {
  const [expandedDay, setExpandedDay] = useState(null);
  const [showSettings, setShowSettings] = useState(false);

  // Save schedule to DB on mount
  const saveScheduleToDB = async (scheduleData) => {
    if (!filePath) return console.error('File path missing for schedule save');

    try {
      const payload = {
        userId: userId || '12345',
        materialId: materialId || '445',
        materialTitle: materialTitle || 'Random Material',
        filePath,
        availableHoursPerDay: 3,
        schedule: scheduleData,
        createdAt: new Date(),
      };

      const response = await axios.post('http://localhost:5000/api/schedule', payload);
      console.log('Schedule saved successfully:', response.data);
    } catch (error) {
      console.error('Error saving schedule:', error.response?.data || error.message);
    }
  };

  useEffect(() => {
    if (schedule && filePath) {
      saveScheduleToDB(schedule);
    }
  }, [schedule, filePath]);

  if (isLoading) return <div className="schedule-container">Loading schedule...</div>;
  if (!schedule) return null;

  const { schedule: days, studyTips, milestones, totalEstimatedHours } = schedule;

  const toggleDay = (dayNum) => setExpandedDay(expandedDay === dayNum ? null : dayNum);

  const formatDuration = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? (mins > 0 ? `${hours}h ${mins}m` : `${hours}h`) : `${mins}m`;
  };

  return (
    <div className="schedule-container">
      <h2>ðŸ“… Your Study Schedule</h2>
      <div>Total Time: {totalEstimatedHours}h</div>
      {days.map((day) => (
        <div key={day.day} className="day-card">
          <div onClick={() => toggleDay(day.day)}>
            Day {day.day} - {formatDuration(day.totalMinutes)} ({day.sessions.length} sessions)
          </div>
          {expandedDay === day.day && (
            <div className="day-sessions">
              {day.sessions.map((s, idx) => (
                <div key={idx}>{s.title} - {formatDuration(s.duration)}</div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default Schedule;
