import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import Summary from './components/Summary';
import Quiz from './components/Quiz';
import { uploadFile, generateSummary, generateQuiz } from './services/api';
import './App.css';

function App() {
  const [uploadedFile, setUploadedFile] = useState(null);
  const [summary, setSummary] = useState(null);
  const [quiz, setQuiz] = useState(null);
  const [isLoadingSummary, setIsLoadingSummary] = useState(false);
  const [isLoadingQuiz, setIsLoadingQuiz] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('upload'); // upload, summary, quiz

  const handleFileUpload = async (file) => {
    try {
      setError(null);
      setActiveTab('upload');
      
      // Upload file
      const uploadResponse = await uploadFile(file);
      setUploadedFile(uploadResponse);
      
      // Automatically generate summary
      setActiveTab('summary');
      setIsLoadingSummary(true);
      
      const summaryResponse = await generateSummary(uploadResponse.filePath);
      setSummary(summaryResponse.summary);
      setIsLoadingSummary(false);
      
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to process file');
      setIsLoadingSummary(false);
      console.error('Upload error:', err);
    }
  };

  const handleGenerateQuiz = async () => {
    if (!uploadedFile) return;
    
    try {
      setError(null);
      setActiveTab('quiz');
      setIsLoadingQuiz(true);
      
      const quizResponse = await generateQuiz(uploadedFile.filePath, {
        numQuestions: 5,
        difficulty: 'medium'
      });
      
      setQuiz(quizResponse.quiz);
      setIsLoadingQuiz(false);
      
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to generate quiz');
      setIsLoadingQuiz(false);
      console.error('Quiz generation error:', err);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <h1>ü§ñ AutoLearn</h1>
          <p className="tagline">Your AI-Powered Study Assistant</p>
        </div>
      </header>

      <main className="app-main">
        {error && (
          <div className="error-banner">
            <span className="error-icon">‚ö†Ô∏è</span>
            {error}
            <button className="error-close" onClick={() => setError(null)}>√ó</button>
          </div>
        )}

        {!uploadedFile ? (
          <div className="upload-section">
            <FileUpload onFileUploaded={handleFileUpload} />
            <div className="features-grid">
              <div className="feature-card">
                <div className="feature-icon">üìù</div>
                <h3>Smart Summaries</h3>
                <p>AI-powered summaries that extract key concepts and main ideas</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">üéØ</div>
                <h3>Auto Quizzes</h3>
                <p>Automatically generate practice quizzes from your materials</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">üìä</div>
                <h3>Track Progress</h3>
                <p>Monitor your learning and identify weak areas</p>
              </div>
            </div>
          </div>
        ) : (
          <>
            <div className="tabs">
              <button
                className={`tab ${activeTab === 'summary' ? 'active' : ''}`}
                onClick={() => setActiveTab('summary')}
              >
                üìù Summary
              </button>
              <button
                className={`tab ${activeTab === 'quiz' ? 'active' : ''}`}
                onClick={() => {
                  setActiveTab('quiz');
                  if (!quiz && !isLoadingQuiz) {
                    handleGenerateQuiz();
                  }
                }}
              >
                üéØ Quiz
              </button>
            </div>

            <div className="content-area">
              {activeTab === 'summary' && (
                <Summary summary={summary} isLoading={isLoadingSummary} />
              )}
              
              {activeTab === 'quiz' && (
                <Quiz quiz={quiz} isLoading={isLoadingQuiz} />
              )}
            </div>

            <div className="action-bar">
              <button
                className="secondary-button"
                onClick={() => {
                  setUploadedFile(null);
                  setSummary(null);
                  setQuiz(null);
                  setActiveTab('upload');
                }}
              >
                üìÑ Upload New File
              </button>
            </div>
          </>
        )}
      </main>

      <footer className="app-footer">
        <p>Powered by Claude AI ‚Ä¢ Built with React & Node.js</p>
      </footer>
    </div>
  );
}

export default App;
