import React, { useState, useEffect } from 'react';
import './Schedule.css';
import axios from 'axios';

function Schedule({ userId, materialId, materialTitle }) {
  const [schedule, setSchedule] = useState([]);
  const [loading, setLoading] = useState(true);

  // Function to fetch/generate schedule (mock or API)
  const fetchSchedule = async () => {
    // You can replace this with your schedule generation logic
    const generatedSchedule = [
      { day: 'Monday', topic: 'Chapter 1', hours: 2 },
      { day: 'Tuesday', topic: 'Chapter 2', hours: 2 },
    ];
    setSchedule(generatedSchedule);
    setLoading(false);
    return generatedSchedule;
  };

  // Function to save schedule to DB
  const saveScheduleToDB = async (scheduleData) => {
    try {
      const payload = {
        userId,
        materialId,
        materialTitle,
        schedule: scheduleData,
        createdAt: new Date(),
      };

      const response = await axios.post('http://localhost:5000/api/schedule', payload);
      console.log('Schedule saved successfully:', response.data);
    } catch (error) {
      console.error('Error saving schedule:', error);
    }
  };

  // useEffect runs when component mounts
  useEffect(() => {
    const init = async () => {
      const generatedSchedule = await fetchSchedule();
      await saveScheduleToDB(generatedSchedule);
    };

    init();
  }, []);

  if (loading) return <div>Loading schedule...</div>;

  return (
    <div className="schedule-container">
      <h2>Study Schedule</h2>
      <table>
        <thead>
          <tr>
            <th>Day</th>
            <th>Topic</th>
            <th>Hours</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <tr key={index}>
              <td>{item.day}</td>
              <td>{item.topic}</td>
              <td>{item.hours}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Schedule;
