import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import Summary from './components/Summary';
import Quiz from './components/Quiz';
import Schedule from './components/Schedule';
import Dashboard from './components/Dashboard';
import { uploadFile, generateSummary, generateQuiz, generateSchedule, adjustSchedule } from './services/api';
import './App.css';

function App() {
  const [uploadedFile, setUploadedFile] = useState(null);
  const [summary, setSummary] = useState(null);
  const [quiz, setQuiz] = useState(null);
  const [schedule, setSchedule] = useState(null);
  const [isLoadingSummary, setIsLoadingSummary] = useState(false);
  const [isLoadingQuiz, setIsLoadingQuiz] = useState(false);
  const [isLoadingSchedule, setIsLoadingSchedule] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('upload'); // upload, summary, quiz, schedule

  const handleFileUpload = async (file) => {
    try {
      setError(null);
      setActiveTab('upload');
      
      // Upload file
      const uploadResponse = await uploadFile(file);
      setUploadedFile(uploadResponse);
      
      // Automatically generate summary
      setActiveTab('summary');
      setIsLoadingSummary(true);
      
      const summaryResponse = await generateSummary(uploadResponse.filePath);
      setSummary(summaryResponse.summary);
      setIsLoadingSummary(false);
      
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to process file');
      setIsLoadingSummary(false);
      console.error('Upload error:', err);
    }
  };

  const handleGenerateQuiz = async () => {
    if (!uploadedFile) return;
    
    try {
      setError(null);
      setActiveTab('quiz');
      setIsLoadingQuiz(true);
      
      const quizResponse = await generateQuiz(uploadedFile.filePath, {
        numQuestions: 5,
        difficulty: 'medium'
      });
      
      setQuiz(quizResponse.quiz);
      setIsLoadingQuiz(false);
      
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to generate quiz');
      setIsLoadingQuiz(false);
      console.error('Quiz generation error:', err);
    }
  };

  const handleGenerateSchedule = async () => {
    if (!uploadedFile) return;
    
    try {
      setError(null);
      setActiveTab('schedule');
      setIsLoadingSchedule(true);
      
      // Calculate target date (2 weeks from now as default)
      const targetDate = new Date();
      targetDate.setDate(targetDate.getDate() + 14);
      
      const scheduleResponse = await generateSchedule(uploadedFile.filePath, {
        availableHoursPerDay: 2,
        targetDate: targetDate.toISOString().split('T')[0],
        difficulty: 'medium',
        learningStyle: 'balanced'
      });
      
      setSchedule(scheduleResponse.schedule);
      setIsLoadingSchedule(false);
      
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to generate schedule');
      setIsLoadingSchedule(false);
      console.error('Schedule generation error:', err);
    }
  };

  const handleAdjustSchedule = async (adjustmentType) => {
    if (!schedule) return;
    
    try {
      setError(null);
      setIsLoadingSchedule(true);
      
      const adjustments = {
        'more-time': 'I need more time per day - extend the schedule to reduce daily workload',
        'faster-pace': 'I want to finish faster - compress the schedule to fewer days',
        'more-practice': 'Add more practice and review sessions throughout the schedule'
      };
      
      const adjustedResponse = await adjustSchedule(
        schedule,
        adjustments[adjustmentType] || adjustmentType
      );
      
      setSchedule(adjustedResponse.schedule);
      setIsLoadingSchedule(false);
      
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to adjust schedule');
      setIsLoadingSchedule(false);
      console.error('Schedule adjustment error:', err);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <h1>ü§ñ AutoLearn</h1>
          <p className="tagline">Your AI-Powered Study Assistant</p>
        </div>
      </header>

      <main className="app-main">
        {error && (
          <div className="error-banner">
            <span className="error-icon">‚ö†Ô∏è</span>
            {error}
            <button className="error-close" onClick={() => setError(null)}>√ó</button>
          </div>
        )}

        {!uploadedFile ? (
          <div className="upload-section">
            <FileUpload onFileUploaded={handleFileUpload} />
            <div className="features-grid">
              <div className="feature-card">
                <div className="feature-icon">üìù</div>
                <h3>Smart Summaries</h3>
                <p>AI-powered summaries that extract key concepts and main ideas</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">üéØ</div>
                <h3>Auto Quizzes</h3>
                <p>Automatically generate practice quizzes from your materials</p>
              </div>
              <div className="feature-card">
                <div className="feature-icon">üìÖ</div>
                <h3>Study Scheduler</h3>
                <p>AI creates personalized study plans with spaced repetition</p>
              </div>
            </div>
          </div>
        ) : (
          <>
            <div className="tabs">
              <button
                className={`tab ${activeTab === 'summary' ? 'active' : ''}`}
                onClick={() => setActiveTab('summary')}
              >
                üìù Summary
              </button>
              <button
                className={`tab ${activeTab === 'quiz' ? 'active' : ''}`}
                onClick={() => {
                  setActiveTab('quiz');
                  if (!quiz && !isLoadingQuiz) {
                    handleGenerateQuiz();
                  }
                }}
              >
                üéØ Quiz
              </button>
              <button
                className={`tab ${activeTab === 'schedule' ? 'active' : ''}`}
                onClick={() => {
                  setActiveTab('schedule');
                  if (!schedule && !isLoadingSchedule) {
                    handleGenerateSchedule();
                  }
                }}
              >
                üìÖ Schedule
              </button>
            </div>

            <div className="content-area">
              {activeTab === 'summary' && (
                <Summary summary={summary} isLoading={isLoadingSummary} />
              )}
              
              {activeTab === 'quiz' && (
                <Quiz quiz={quiz} isLoading={isLoadingQuiz} />
              )}
              
              {activeTab === 'schedule' && (
                <Schedule 
                  schedule={schedule} 
                  isLoading={isLoadingSchedule}
                  onAdjust={handleAdjustSchedule}
                />
              )}
            </div>

            <div className="action-bar">
              <button
                className="secondary-button"
                onClick={() => {
                  setUploadedFile(null);
                  setSummary(null);
                  setQuiz(null);
                  setSchedule(null);
                  setActiveTab('upload');
                }}
              >
                üìÑ Upload New File
              </button>
            </div>
          </>
        )}
      </main>

      <footer className="app-footer">
        <p>Powered by Claude AI ‚Ä¢‚ú®</p>
      </footer>
    </div>
  );
}

export default App;
