import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

/**
 * Upload a PDF file
 */
export const uploadFile = async (file) => {
  const formData = new FormData();
  formData.append('file', file);

  const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });

  return response.data;
};

/**
 * Generate summary from uploaded file
 */
export const generateSummary = async (filePath, customPrompt = '') => {
  const response = await axios.post(`${API_BASE_URL}/summarize`, { filePath, customPrompt });
  return response.data;
};

/**
 * Generate quiz from uploaded file
 */
export const generateQuiz = async (filePath, options = {}) => {
  const { numQuestions = 5, difficulty = 'medium' } = options;

  const response = await axios.post(`${API_BASE_URL}/quiz`, {
    filePath,
    numQuestions,
    difficulty,
  });

  return response.data;
};

/**
 * Generate study schedule from uploaded file
 */
export const generateSchedule = async (filePath, options = {}) => {
  const { 
    availableHoursPerDay = 2,
    targetDate = null,
    difficulty = 'medium',
    learningStyle = 'balanced'
  } = options;

  const response = await axios.post(`${API_BASE_URL}/schedule`, {
    filePath,
    availableHoursPerDay,
    targetDate,
    difficulty,
    learningStyle
  });

  return response.data;
};

/**
 * Adjust existing schedule
 */
export const adjustSchedule = async (currentSchedule, adjustment) => {
  const response = await axios.post(`${API_BASE_URL}/schedule/adjust`, {
    currentSchedule,
    adjustment
  });

  return response.data;
};

/**
 * Mark a session as started
 */
export const startSession = async (scheduleId, day, sessionIndex) => {
  const response = await axios.post(`${API_BASE_URL}/schedule/${scheduleId}/start`, {
    day,
    sessionIndex
  });

  return response.data;
};

/**
 * Mark a session as completed
 */
export const completeSession = async (scheduleId, day, sessionIndex) => {
  const response = await axios.post(`${API_BASE_URL}/schedule/${scheduleId}/complete`, {
    day,
    sessionIndex
  });

  return response.data;
};

/**
 * Check API health
 */
export const checkHealth = async () => {
  const response = await axios.get(`${API_BASE_URL.replace('/api','')}/health`);
  return response.data;
};
