import React, { useState } from 'react';
import axios from 'axios';
import './Schedule.css';

function Schedule({ schedule, isLoading, onAdjust }) {
  const [expandedDay, setExpandedDay] = useState(null);
  const [showSettings, setShowSettings] = useState(false);
  const [localProgress, setLocalProgress] = useState(schedule?.progress || { startedSessions: [], completedSessions: [] });

  if (isLoading) {
    return (
      <div className="schedule-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Creating your personalized study schedule...</p>
          <p className="loading-subtext">Analyzing content and optimizing learning path</p>
        </div>
      </div>
    );
  }

  if (!schedule) return null;

  const { schedule: days, studyTips, milestones, totalEstimatedHours } = schedule.scheduleData;

  const toggleDay = (dayNum) => setExpandedDay(expandedDay === dayNum ? null : dayNum);

  const getTypeIcon = (type) => ({ reading: 'üìñ', practice: '‚úçÔ∏è', quiz: 'üéØ', review: 'üîÑ' }[type] || 'üìö');
  const getPriorityColor = (priority) => ({ high: '#f56565', medium: '#ed8936', low: '#48bb78' }[priority] || '#718096');
  const formatDuration = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? (mins > 0 ? `${hours}h ${mins}m` : `${hours}h`) : `${mins}m`;
  };

  const totalDays = days.length;
  const completionPercentage = localProgress?.percentComplete || 0;

  // ‚úÖ Start a session
  const startSession = async (dayNum, sessionIndex) => {
    if (!schedule?._id) return alert('Schedule not saved yet!');
    try {
      const res = await axios.post(`http://localhost:5000/api/schedule-progress/${schedule._id}/start-session`, {
        day: dayNum,
        sessionIndex
      });

      if (res.data.success) {
        setLocalProgress(res.data.progress);
        alert(`Session started! Progress: ${res.data.progress.percentComplete}%`);
      }
    } catch (err) {
      console.error('Failed to start session:', err);
      alert('Failed to start session. Check console.');
    }
  };

  // ‚úÖ Complete a session
  const completeSession = async (dayNum, sessionIndex) => {
    if (!schedule?._id) return alert('Schedule not saved yet!');
    try {
      const res = await axios.post(`http://localhost:5000/api/schedule-progress/${schedule._id}/complete-session`, {
        day: dayNum,
        sessionIndex
      });

      if (res.data.success) {
        setLocalProgress(res.data.progress);
        alert(`Session completed! Progress: ${res.data.progress.percentComplete}%`);
      }
    } catch (err) {
      console.error('Failed to complete session:', err);
      alert('Failed to complete session. Check console.');
    }
  };

  const isSessionStarted = (dayNum, sessionIndex) =>
    localProgress?.startedSessions?.some(s => s.day === dayNum && s.sessionIndex === sessionIndex);

  const isSessionCompleted = (dayNum, sessionIndex) =>
    localProgress?.completedSessions?.some(s => s.day === dayNum && s.sessionIndex === sessionIndex);

  return (
    <div className="schedule-container">
      {/* Header */}
      <div className="schedule-header">
        <div className="header-content">
          <h2>üìÖ Your Study Schedule</h2>
          <button className="settings-button" onClick={() => setShowSettings(!showSettings)}>‚öôÔ∏è Adjust</button>
        </div>
        <div className="schedule-stats">
          <div className="stat-card"><span className="stat-label">Total Time</span><span className="stat-value">{totalEstimatedHours}h</span></div>
          <div className="stat-card"><span className="stat-label">Duration</span><span className="stat-value">{totalDays} days</span></div>
          <div className="stat-card"><span className="stat-label">Progress</span><span className="stat-value">{completionPercentage}%</span></div>
        </div>
      </div>

      {showSettings && (
        <div className="settings-panel">
          <h3>Adjust Schedule</h3>
          <div className="settings-options">
            <button onClick={() => onAdjust('more-time')}>Need More Time</button>
            <button onClick={() => onAdjust('faster-pace')}>Faster Pace</button>
            <button onClick={() => onAdjust('more-practice')}>More Practice Sessions</button>
          </div>
        </div>
      )}

      <div className="schedule-timeline">
        {days.map((day, index) => {
          const isExpanded = expandedDay === day.day;
          const hasMilestone = milestones?.find(m => m.day === day.day);

          return (
            <div key={day.day} className={`day-card ${isExpanded ? 'expanded' : ''}`}>
              <div className="day-header" onClick={() => toggleDay(day.day)}>
                <div className="day-info">
                  <span className="day-number">Day {day.day}</span>
                  <span className="day-date">{new Date(day.date).toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' })}</span>
                </div>
                <div className="day-summary">
                  <span className="session-count">{day.sessions.length} sessions</span>
                  <span className="day-duration">{formatDuration(day.totalMinutes)}</span>
                  <span className="expand-icon">{isExpanded ? '‚ñº' : '‚ñ∂'}</span>
                </div>
              </div>

              {hasMilestone && <div className="milestone-badge">üèÜ Milestone: {hasMilestone.milestone}</div>}
              <div className="day-goal"><strong>Goal:</strong> {day.dailyGoal}</div>

              {isExpanded && (
                <div className="day-sessions">
                  {day.sessions.map((session, idx) => (
                    <div key={idx} className="session-card">
                      <div className="session-header">
                        <span className="session-icon">{getTypeIcon(session.type)}</span>
                        <span className="session-title">{session.title}</span>
                        <span className="session-priority" style={{ backgroundColor: getPriorityColor(session.priority) }}>{session.priority}</span>
                      </div>
                      <div className="session-details">
                        <p className="session-description">{session.description}</p>
                        <div className="session-topics">
                          <strong>Topics:</strong>
                          <div className="topic-tags">{session.topics.map((t, i) => <span key={i} className="topic-tag">{t}</span>)}</div>
                        </div>
                        <div className="session-meta">
                          <span className="session-duration">‚è±Ô∏è {formatDuration(session.duration)}</span>
                          <span className="session-type">{session.type}</span>
                        </div>
                      </div>
                      <div className="session-actions">
                        <button
                          className="action-button start"
                          onClick={() => startSession(day.day, idx)}
                          disabled={isSessionStarted(day.day, idx)}
                        >
                          {isSessionStarted(day.day, idx) ? 'Started' : 'Start Session'}
                        </button>
                        <button
                          className="action-button complete"
                          onClick={() => completeSession(day.day, idx)}
                          disabled={isSessionCompleted(day.day, idx)}
                        >
                          {isSessionCompleted(day.day, idx) ? 'Completed' : 'Mark Complete'}
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          );
        })}
      </div>

      {studyTips?.length > 0 && (
        <div className="study-tips">
          <h3>üí° Study Tips</h3>
          <ul>{studyTips.map((tip, i) => <li key={i}>{tip}</li>)}</ul>
        </div>
      )}

      {milestones?.length > 0 && (
        <div className="milestones-section">
          <h3>üéØ Learning Milestones</h3>
          <div className="milestones-grid">
            {milestones.map((m, i) => (
              <div key={i} className="milestone-card">
                <div className="milestone-day">Day {m.day}</div>
                <div className="milestone-title">{m.milestone}</div>
                <div className="milestone-assessment">{m.assessment}</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Schedule;
